<template>
 <b-modal
      :visible="showDialog"
      @change="(val) => $emit('update:showDialog',val)"
      @shown="psToBottom()"
      :size="size"
      hide-backdrop
      centered
      no-close-on-backdrop
      body-class="p-0 chat-application"
      @show="chatMostrado"
      @hide="$emit('chatCerrado')">

      <template #modal-header>
        <div class="d-flex align-items-center">
            <b-avatar
              size="34"
              :src="`${usuario.avatar}`"
              class="mr-50 badge-minimal"
              badge
              badge-variant="success"
            />
            <h5 class="mb-0">
              {{`${usuario.nombre} ${usuario.apellido}`}}
            </h5>
          </div>

          <b-button @click="() => $emit('update:showDialog',false)" size="sm" variant="outline-danger" title="Cerrar" v-b-tooltip.hover>
             <feather-icon
               icon="XIcon"/>
          </b-button>
          
      </template>
      <b-card class="chat-widget" no-body >


        <section class="chat-app-window">
           <!-- usuario_id,usuario_avatar,mensaje -->
          <vue-perfect-scrollbar ref="refChatLogPS" :settings="perfectScrollbarSettings" class="user-chats scroll-area w-100" >
              <div class="chats">
                  <div v-for="(mensaje,index) in chat" :key="index" class="chat"  :class="{'chat-left': usuario.id != mensaje.usuario_id}">
                     <div class="chat-avatar">
                        <b-avatar size="36" class="avatar-border-2 box-shadow-1" variant="transparent" 
                        v-b-tooltip.hover
                        :src="usuario.id === mensaje.usuario_id ? usuario.avatar : `/storage/img-perfil/${mensaje.usuario.imagen}`"  
                        :title="`${mensaje.usuario.rol.nombre.toUpperCase()}: ${mensaje.usuario.nombre}`" 
                        style="cursor:pointer"/>
                     </div>
                     <div class="chat-body">
                        <div class="chat-content">
                        <p>{{ mensaje.mensaje }}</p>
                     </div>
                     </div>
                  </div>
               </div>
         </vue-perfect-scrollbar>

        </section>
      </b-card>

      <template #modal-footer>

        <b-row class="w-100">
          <b-col cols="12 px-0">
             <b-form class="chat-app-form" @submit.prevent="enviarMensaje" >
              <b-input-group class="input-group-merge form-send-message mr-1">
                <b-form-input v-model="mensaje" placeholder="Enter para enviar"/>

                <b-input-group-append>
                   <b-button variant="primary" type="submit" :disabled="(mensaje.length <= 2)" v-loading="loading" >
                      <feather-icon icon="SendIcon"/>
                      Enviar
                  </b-button>
                </b-input-group-append>
              </b-input-group>
             
            </b-form>
          </b-col>
        </b-row>

         


      </template>
      


    
    </b-modal>
</template>
<script>

import {
   BModal,
   BAvatar,
   BCard,
   BRow,
   BCol,
   BForm,
   BFormInput,
   BInputGroup,
   BInputGroupAppend,
   BButton,
   VBTooltip

} from 'bootstrap-vue'

import {toRefs,watch,ref,nextTick,onUpdated,computed} from 'vue'

import store  from '@/store'

import VuePerfectScrollbar from 'vue-perfect-scrollbar'

export default {

   components:{
      BModal,
      VuePerfectScrollbar,
      BAvatar,
      BCard,
      BRow,
      BCol,
      BForm,
      BFormInput,
      BInputGroup,
      BInputGroupAppend,
      BButton
   },

   directives:{
      'b-tooltip':VBTooltip
   },

   props:{

      chat:{
         type:Array,
         required:true,
         default:[]
      },

      showDialog:{
         type:Boolean,
         default:false
      },

      size:{
         type:String,
         default:'lg'
      },

      id:{
         type:Number|null,
         required:false,

      },
      
      // canal:{
      //    type:String,
      //    required:false
      // }



   },


   setup(props,{emit}) {
      
      let {usuario} = toRefs(store.state.usuario)
      
      let mensaje = ref('')

      let { chat,canal } = toRefs(props)


      let perfectScrollbarSettings = ref({
         maxScrollbarLength: 150,
         wheelPropagation: false,
      })

      let refChatLogPS = ref(null)

      const enviarMensaje = () => {
        
         emit('handleSubmit',{mensaje:mensaje.value,id:props.id})
         mensaje.value = '';
         
         nextTick(() => {
            psToBottom()
         })
      }

      const psToBottom = ()  => {
         let scrollEl = refChatLogPS.value.$el || refChatLogPS.value
         scrollEl.scrollTop = scrollEl.scrollHeight
      }

      watch(chat,() => {
         if(refChatLogPS.value){
            setTimeout(() => {
               psToBottom();

            },200)
         }
      })


      const chatMostrado = () => {

      

      } 
 


      return {
         usuario,
         perfectScrollbarSettings,
         refChatLogPS,
         mensaje,
         enviarMensaje,
         loading:computed(() => store.state.loading),
         psToBottom,
         chatMostrado
        
      }

   },

}
</script>

